import { Component, OnInit } from '@angular/core';
import { LandLord } from 'src/app/models/LandLord';
import { LandLordService } from 'src/app/services/land-lord.service';
import { ActivatedRoute } from '@angular/router';
@Component({
  selector: 'app-land-lord-form',
  templateUrl: './land-lord-form.component.html',
  styleUrls: ['./land-lord-form.component.css']
})
export class LandLordFormComponent implements OnInit {
  landLord: LandLord = {
    idLandLord: 0,
    firstName : "",
    lastName: "",
    email: "",
    phoneNumber: ""
  };

  selectedId: string = '';

  ngOnInit(): void {

  }
  constructor(private landLordService: LandLordService,private activatedRoute:ActivatedRoute){

  }

  cargar():void{
    this.activatedRoute.params.subscribe(
      e=>{
        let id = e['id'];
        if(id){
          this.landLordService.getLandLord(id).subscribe(
            es=>this.landLord=es,
            this.selectedId = id
          );
        }
      }
    )
  }
  update():void{
    this.landLordService.updateLandlord(this.selectedId,this.landLord).subscribe(
      res => {console.log(res)},
      err => console.error(err)
    );
  }

  saveNewLandLord(){
    delete this.landLord.idLandLord;

    this.landLordService.saveLandLord(this.landLord).subscribe(
      res =>{console.log(res)},
      err => console.error(err)
    );
  }



}
<div class="col-md-4-offset-md-2">
    <div class="card">
        <div class="card-body">
            <form>
                <div class="form-group">
                    <input type="text" name="firstName" [(ngModel)]="landLord.firstName" placeholder="Enter firstName" class="form-control" autofocus>
                </div>
                <br>
                <div class="form-group">
                    <input type="text" name="lastName" [(ngModel)]="landLord.lastName" placeholder="Enter lastName" class="form-control" autofocus>
                </div>
                <br>
                <div class="form-group">
                    <input type="text" name="email" [(ngModel)]="landLord.email" placeholder="Enter email " class="form-control" autofocus>
                </div>
                <div class="form-group" style="padding-top: 25px;">
                    <input type="text" name="phoneNumber" [(ngModel)]="landLord.phoneNumber" placeholder="Enter phoneNumber " class="form-control" autofocus>
                </div>
                <br>
                <button class="btn btn-success btn-block" (click)="saveNewLandLord()">Save</button>
            </form>
        </div>
    </div>    
</div>
<div class="col-md-4">
    <div class="card card-body text-center">
        <h1>{{landLord.firstName}} - {{landLord.lastName}}</h1>
        <h2>{{landLord.email}} - {{landLord.phoneNumber}}</h2>

    </div>
</div>
