import { Component, OnInit } from '@angular/core';
import { Property } from 'src/app/models/Property';
import { PropertyService } from 'src/app/services/property.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-property-form',
  templateUrl: './property-form.component.html',
  styleUrls: ['./property-form.component.css']
})
export class PropertyFormComponent implements OnInit{
  property: Property = {
    idProperty: 0,
    propertyType: "",
    streetAddress: "",
    city: "",
    stateProvince: "",
    zipCode:"",
    price: 0,
    numBedrooms: 0,
    numBathrooms: 0, 
    landMeters: 0,
    constructionMeters: 0,
    photo: "",
    garageSize: 0,
    sisternSize: 0,
    bonus: ""
  }

  selectedId: string = '';

  ngOnInit(): void {
    this.cargar();
  }
  constructor(private propertyService: PropertyService, private activatedRoute:ActivatedRoute){

  }


  cargar():void{
    this.activatedRoute.params.subscribe(
      e=>{
        let id = e['idProperty'];
        if(id){
          this.propertyService.get(id).subscribe(
            es=>this.property=es,
            this.selectedId = id
          );
        }
      }
    )
  }

  update():void{
    this.propertyService.update(this.selectedId,this.property).subscribe(
      res => {console.log(res)},
      err => console.error(err)
    );
  }

  saveNewProperty(){
    delete this.property.idProperty;

    this.propertyService.save(this.property).subscribe(
      res =>{console.log(res)},
      err => console.error(err)
    );
  }
}
<div class="col-md-4-offset-md-2">
    <div class="card">
        <div class="card-body">
            <form>
                <div class="form-group">
                    <input type="text" name="propertyType" [(ngModel)]="property.propertyType" placeholder="Enter Property Type" class="form-control" autofocus>
                </div>
                <br>
                <div class="form-group">
                    <input type="text" name="streetAddress" [(ngModel)]="property.streetAddress" placeholder="Enter Street Address" class="form-control" autofocus>
                </div>
                <br>
                <div class="form-group">
                    <input type="text" name="city" [(ngModel)]="property.city" placeholder="Enter city " class="form-control" autofocus>
                </div>
                <br>
                <div class="form-group" style="padding-top: 25px;">
                    <input type="text" name="stateProvince" [(ngModel)]="property.stateProvince" placeholder="Enter state " class="form-control" autofocus>
                </div>
                <br>
                <button class="btn btn-success btn-block" (click)="saveNewProperty()" routerLink="/Properties">Save</button>
                <button class="btn btn-success btn-block" (click)="update()" routerLink="/Properties" style="padding-left: 10px;">Update</button>
            </form>
        </div>
    </div>    
</div>
<div class="col-md-4">
    <div class="card card-body text-center">
        <h1>{{property.propertyType}} - {{property.streetAddress}}</h1>
        <h2>{{property.city}} - {{property.stateProvince}}</h2>

    </div>
</div>
